
public class Prog10_If_Else_Ladder {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int mark=70;
		if (mark<35) {
			System.out.println("Fail!");
		}
		else if (mark<70) {
			System.out.println("Just Pass!");
		}
		if (mark>=70) {
			System.out.println("Distinction!");
		}
	}

}
